consent_override.sql:
UPDATE jobs
SET 
  consent_status = 'GIVEN',
  consent_timestamp = NOW()
WHERE id = {{selected_job_id}}
RETURNING id, job_id, phone, consent_status, consent_timestamp;

denial_override.sql:
UPDATE jobs
SET 
  consent_status = 'DENIED',
  consent_timestamp = NOW()
WHERE id = {{selected_job_id}}
RETURNING id, job_id, phone, consent_status, consent_timestamp;

payment_confirmation.sql:
UPDATE jobs
SET 
  status = 'Paid',
  consent_status = 'GIVEN'
WHERE id = {{selected_job_id}}
RETURNING id, job_id, phone, status;
